INSERT INTO `jos_jreviews_paid_emails` VALUES ('4', 'User Order Processed', 'Triggered when payment handler notifies script of payment results', 'user_order_processed', 'Payment received for: {listing_title}', 'Dear {user_name},\n\nWe have received your payment for the following order #{order_id} and your listing will now be published.\n\nListing: {listing_title}\nUrl: {listing_url}\nAmount: {order_amount}\nPlan: {plan_name} - {plan_description}\nOrder expires: {order_expires}\nOrder amount:{order_amount}\n\nThank you,\n\nThe Administration\n{site_url}', '', '0', '4');
